mapper.select:
select *
from userTB
where id = #{id} AND password = #{password}

select id
from userTB
where id=#{id}

mapper.update:
update userTB
set n_id = #{nId}
where id = #{userId}

mapper.select:
select *
from addressTB
where id = #{id} and aNum = #{aNum}

mapper.update:
update addressTB
set basic=false
where id=#{id}

update addressTB
set name=${name}, address=${address}, detail=#{detail}, zipCode=#{zipCode},basic=#{basic}
where id=#{id} and aNum=#{aNum}
